import React from "react";
import { useParams } from "react-router-dom";

interface Post {
  id: string;
  title: string;
  content: string;
  author: string;
  date: string;
  category: string;
}

interface Comment {
  postId: string;
  author: string;
  text: string;
}

const posts: Post[] = [
  { id: "1", title: "Welcome to the Blog", content: "This is your first post! **Enjoy blogging!**", author: "Admin", date: "2024-06-23", category: "General" },
  { id: "2", title: "React 19 Features", content: "A look at what's new in React 19. _Stay tuned!_", author: "Jane", date: "2024-06-22", category: "Tech" },
];

const comments: Comment[] = [
  { postId: "1", author: "Jane", text: "Great start!" },
  { postId: "1", author: "John", text: "Looking forward to more posts." },
];

const Post: React.FC = () => {
  const { id } = useParams<{ id: string }>();
  const post = posts.find(p => p.id === id);
  const postComments = comments.filter(c => c.postId === id);

  if (!post) return <div>Post not found.</div>;

  return (
    <div>
      <h1>{post.title}</h1>
      <div style={{ marginBottom: "1em", color: "#555" }}>
        <strong>Author:</strong> {post.author} | <strong>Date:</strong> {post.date} | <strong>Category:</strong> {post.category}
      </div>
      <div style={{ marginBottom: "1em", background: "#f9f9f9", padding: "0.5em 1em", borderRadius: "6px" }}>
        <strong>About the author:</strong> <span style={{ color: "#888" }}>[Profile coming soon]</span>
      </div>
      <div style={{ margin: "1em 0" }}>{post.content}</div>
      <button disabled style={{ marginBottom: "1em", opacity: 0.5 }}>�� Like (0)</button>
      <h2>Comments</h2>
      <ul>
        {postComments.map((c, i) => (
          <li key={i}><strong>{c.author}:</strong> {c.text}</li>
        ))}
      </ul>
      <div style={{ marginTop: "1em" }}>
        <input type="text" placeholder="Add a comment..." style={{ width: "60%", marginRight: "0.5em" }} disabled />
        <button disabled style={{ opacity: 0.5 }}>Post</button>
      </div>
    </div>
  );
};

export default Post;
